(37, 'wc-admin-install-jp-and-wcs-plugins', 'info', 'en_US', 'Uh oh... There was a problem during the Jetpack and WooCommerce Shipping & Tax install. Please try again.', 'We noticed that there was a problem during the Jetpack and WooCommerce Shipping &amp; Tax install. Please try again and enjoy all the advantages of having the plugins connected to your store! Sorry for the inconvenience. The \"Jetpack\" and \"WooCommerce Shipping &amp; Tax\" plugins will be installed &amp; activated for free.', '{}', 'unactioned', 'woocommerce-admin', '2023-01-31 19:08:36', NULL, 0, 'plain', '', 0, 1, 'info'),
(38, 'wc-payments-notes-set-up-refund-policy', 'info', 'en_US', 'Set up refund policy', 'Protect your merchant account from fraudulent disputes by defining the policy and making it accessible to customers.', '{}', 'unactioned', 'woocommerce-payments', '2023-01-31 19:11:52', NULL, 0, 'plain', '', 0, 1, 'info'),
